import React, { useEffect, useState } from "react";
import io from "socket.io-client";

function Home() {
  const [inputValue, setInputValue] = useState("");
  const [socket, setSocket] = useState(null);

  // Establish socket connection on component mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const newSocket = io("http://localhost:3001", {
        auth: { token },
      });
      setSocket(newSocket);
      

      // Cleanup on unmount
      return () => newSocket.close();
    }
  }, []);

  // Handle button click
  const handleClick = async () => {
    if (socket) {
      const response = await fetch("http://localhost:3000/api/notifications", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ message: inputValue }),
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      console.log("Success:", data);
    }
  };

  return (
    <div className="container mt-5">
      <h2>Send Notification</h2>
      <div className="form-group">
        <label>Input</label>
        <input
          type="text"
          className="form-control"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
      </div>
      <button className="btn btn-primary mt-3" onClick={handleClick}>
        Send
      </button>
       <h2 className="mt-5">Notifications</h2>
      <ul>
        {notifications.map((notification) => ( // Render notifications from state
          <li key={notification._id}>{notification.message}</li> // Display each notification message
        ))}
      </ul>
    </div>
  );
}

export default Home;
